package Coursera.SolvingProblemsWithSoftware.Week2.StringsThirdAssignments;

import java.util.Locale;

public class Part2 {
    double cgRatio(String dna) {
        int cCount=0;
        int gCount=0;
        for(char c: dna.toUpperCase().toCharArray()){
            if(c=='C')
                cCount++;
            else if(c=='G')
                gCount++;
        }
        return (double)cCount/gCount;
    }

    void testCgRatio() {
        String dna = "CCccGgG";
        System.out.println("Ratio of C:G in "+ dna + ": "+ cgRatio(dna));
    }

    int countCTG(String dna) {
        int countCTG=0;
        int index=dna.indexOf("CTG");
        while(index!=-1) {
            countCTG++;
            index=dna.indexOf("CTG",index+3);
        }
        return countCTG;
    }

    void testCountCTG() {
        String dna="";
        System.out.println("Count of CTG in " + dna + ": "+ countCTG(dna));
    }

    public static void main(String args[]) {
        new Part2().testCgRatio();
        new Part2().testCountCTG();
    }
}
